package com.example.homework37

import android.content.Context
import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import com.example.homework37.databinding.FragmentRecyclerBinding

class RecyclerFragment : Fragment() {

    private var _binding: FragmentRecyclerBinding? = null
    private val binding get() = _binding!!
    private var celebritiesAdapter: CelebritiesAdapter? = null
    private val celebrities = mutableListOf<CelebritiesModel>()

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?
    ): View {
        _binding = FragmentRecyclerBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        addCinemas()
        setupRecyclerView()
    }

    private fun addCinemas() {
        addCelebrity(
            "Billie Eilish",
            "https://www.usmagazine.com/wp-content/uploads/2023/03/Billie-Eilish-more-celebrities-who-took-social-media-breaks-social.jpg?quality=86&strip=all"
        )
        addCelebrity(
            "Michael Jordan",
            image = "data:image/jpeg;base64,"
        )
        addCelebrity(
            name = "LeBron James",
            image = "data:image/jpeg;base64,"
        )
        addCelebrity(
            name = "Drake",
            image = "data:image/jpeg;base64,"
        )
        addCelebrity(
            name = "Justin Timberlake",
            image = "data:image/jpeg;base64,"
        )
        addCelebrity(
            name = "John Wall",
            image = "data:image/jpeg;base64,"
        )
        addCelebrity(
            name = "Harry Potter",
            image = "https://upload.wikimedia.org/wikipedia/en/d/d7/Harry_Potter_character_poster.jpg"
        )
        addCelebrity(
            name = "Cristiano Ronaldo",
            image = "https://upload.wikimedia.org/wikipedia/commons/thumb/d/d7/Cristiano_Ronaldo_playing_for_Al_Nassr_FC_against_Persepolis%2C_September_2023_%28cropped%29.jpg/640px-Cristiano_Ronaldo_playing_for_Al_Nassr_FC_against_Persepolis%2C_September_2023_%28cropped%29.jpg"
        )
    }
    private fun setupRecyclerView() {
        celebritiesAdapter = CelebritiesAdapter(celebrities)
        binding.rvCelebrities.adapter = celebritiesAdapter
    }

    private fun addCelebrity(name: String, image: String) {
        celebrities.add(
            CelebritiesModel(
                name = name,
                image = image
            )
        )
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
        celebritiesAdapter = null
    }
}